// ---- App.js ----
// This is the main component that manages which page is currently visible.
import React, { useState } from 'react';

// You would typically have these components in separate files.
// For this example, they are all included here.

// --- LoginPage Component ---
const LoginPage = ({ navigateTo, setAuthToken }) => {
    const [email, setEmail] = useState('davido@example.com');
    const [password, setPassword] = useState('password');
    const [error, setError] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        // In a real app, you would fetch from your deployed backend URL
        // const response = await fetch('http://YOUR_AZURE_VM_IP:8080/api/users/login', { ... });
        // For this mockup, we'll simulate a successful login.
        if (email && password) {
            const fakeToken = 'fake-jwt-token';
            setAuthToken(fakeToken);
            localStorage.setItem('token', fakeToken);
            navigateTo('dashboard');
        } else {
            setError('Invalid credentials');
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-slate-50">
            <div className="max-w-md w-full bg-white p-8 rounded-xl shadow-lg">
                <h2 className="text-3xl font-black text-center text-[#073B4C] mb-2">GiveAway</h2>
                <p className="text-center text-slate-600 mb-8">Welcome Back</p>
                <form onSubmit={handleLogin}>
                    <div className="mb-4">
                        <label htmlFor="email" className="block text-sm font-medium text-slate-700">Email Address</label>
                        <input type="email" id="email" value={email} onChange={e => setEmail(e.target.value)} className="mt-1 block w-full px-3 py-2 bg-white border border-slate-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500" required />
                    </div>
                    <div className="mb-6">
                        <label htmlFor="password" className="block text-sm font-medium text-slate-700">Password</label>
                        <input type="password" id="password" value={password} onChange={e => setPassword(e.target.value)} className="mt-1 block w-full px-3 py-2 bg-white border border-slate-300 rounded-md shadow-sm" required />
                    </div>
                    {error && <p className="text-red-500 text-sm text-center mb-4">{error}</p>}
                    <button type="submit" className="w-full bg-[#118AB2] text-white font-semibold py-3 rounded-lg hover:bg-[#0d6c8a]">Login</button>
                </form>
                <p className="text-center text-sm text-slate-500 mt-6">
                    Don't have an account? <a href="#" onClick={() => navigateTo('register')} className="font-medium text-[#118AB2] hover:underline">Sign up</a>
                </p>
            </div>
        </div>
    );
};

// --- RegisterPage Component ---
const RegisterPage = ({ navigateTo }) => {
     const handleRegister = (e) => {
        e.preventDefault();
        // In a real app, you would make an API call to register
        // For now, we just navigate to the login page
        alert('Registration successful! Please log in.');
        navigateTo('login');
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-slate-50">
            <div className="max-w-md w-full bg-white p-8 rounded-xl shadow-lg">
                <h2 className="text-3xl font-black text-center text-[#073B4C] mb-2">Create Your Account</h2>
                <p className="text-center text-slate-600 mb-8">Join the best giveaway platform.</p>
                <form onSubmit={handleRegister}>
                    <div className="mb-4">
                        <label htmlFor="reg-email" className="block text-sm font-medium text-slate-700">Email Address</label>
                        <input type="email" id="reg-email" className="mt-1 block w-full px-3 py-2 bg-white border border-slate-300 rounded-md shadow-sm" required />
                    </div>
                    <div className="mb-4">
                        <label htmlFor="reg-password" className="block text-sm font-medium text-slate-700">Password</label>
                        <input type="password" id="reg-password" className="mt-1 block w-full px-3 py-2 bg-white border border-slate-300 rounded-md shadow-sm" required />
                    </div>
                    <div className="mb-6">
                        <label htmlFor="accountType" className="block text-sm font-medium text-slate-700">I am a(n)...</label>
                        <select id="accountType" className="mt-1 block w-full px-3 py-2 bg-white border border-slate-300 rounded-md shadow-sm">
                            <option>Individual / Influencer</option>
                            <option>Corporation / Brand</option>
                            <option>Bank / Fintech</option>
                        </select>
                    </div>
                    <button type="submit" className="w-full bg-[#118AB2] text-white font-semibold py-3 rounded-lg hover:bg-[#0d6c8a]">Create Account</button>
                </form>
                <p className="text-center text-sm text-slate-500 mt-6">
                    Already have an account? <a href="#" onClick={() => navigateTo('login')} className="font-medium text-[#118AB2] hover:underline">Login</a>
                </p>
            </div>
        </div>
    );
};

// --- DashboardPage Component ---
const DashboardPage = ({ authToken }) => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [mode, setMode] = useState('random');

    // Mock data
    const campaigns = [
        { title: 'Friday Flex', status: 'Completed', participants: 5102, date: 'July 25, 2025' },
        { title: 'End of Month Special', status: 'Completed', participants: 8450, date: 'July 31, 2025' },
        { title: 'August Giveaway', status: 'Live', participants: 1234, date: 'August 11, 2025' },
    ];

    const handleCreateCampaign = (e) => {
        e.preventDefault();
        // In a real app, you would send this data to your backend API
        alert('Campaign created successfully! (Mocked)');
        setIsModalOpen(false);
    }

    return (
        <div>
            <header className="bg-white shadow-sm">
                <div className="container mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex justify-between items-center h-16">
                        <h1 className="text-2xl font-black text-[#073B4C]">GiveAway</h1>
                        <div className="flex items-center">
                            <p className="mr-4 font-semibold hidden sm:block">Welcome, Davido</p>
                            <img className="h-10 w-10 rounded-full" src="https://placehold.co/100x100/FFD166/073B4C?text=D" alt="User Avatar" />
                        </div>
                    </div>
                </div>
            </header>

            <main className="container mx-auto p-4 sm:p-6 lg:p-8">
                <div className="flex justify-between items-center mb-8">
                    <h2 className="text-3xl font-bold text-[#073B4C]">Dashboard</h2>
                    <button onClick={() => setIsModalOpen(true)} className="bg-[#118AB2] text-white font-semibold py-3 px-6 rounded-lg hover:bg-[#0d6c8a]">Create New Giveaway</button>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
                    <div className="bg-white p-6 rounded-lg shadow"><h3 className="text-slate-500">Total Giveaways</h3><p className="text-4xl font-bold text-[#073B4C] mt-2">12</p></div>
                    <div className="bg-white p-6 rounded-lg shadow"><h3 className="text-slate-500">Total Amount Given</h3><p className="text-4xl font-bold text-[#073B4C] mt-2">₦15,200,000</p></div>
                    <div className="bg-white p-6 rounded-lg shadow"><h3 className="text-slate-500">Total Participants</h3><p className="text-4xl font-bold text-[#073B4C] mt-2">250,000+</p></div>
                </div>

                <div>
                    <h3 className="text-2xl font-bold text-[#073B4C] mb-4">Campaign History</h3>
                    <div className="bg-white rounded-lg shadow overflow-x-auto">
                        <table className="min-w-full divide-y divide-slate-200">
                            <thead className="bg-slate-50">
                                <tr>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-slate-500 uppercase tracking-wider">Campaign</th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-slate-500 uppercase tracking-wider">Status</th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-slate-500 uppercase tracking-wider">Participants</th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-slate-500 uppercase tracking-wider">Date</th>
                                </tr>
                            </thead>
                            <tbody className="bg-white divide-y divide-slate-200">
                                {campaigns.map((campaign, index) => (
                                    <tr key={index}>
                                        <td className="px-6 py-4 whitespace-nowrap font-medium">{campaign.title}</td>
                                        <td className="px-6 py-4 whitespace-nowrap">
                                            <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${campaign.status === 'Completed' ? 'bg-green-100 text-green-800' : 'bg-blue-100 text-blue-800'}`}>
                                                {campaign.status}
                                            </span>
                                        </td>
                                        <td className="px-6 py-4 whitespace-nowrap">{campaign.participants.toLocaleString()}</td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-slate-500">{campaign.date}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </main>

            {isModalOpen && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
                    <div className="bg-white p-8 rounded-lg shadow-xl w-full max-w-lg">
                        <div className="flex justify-between items-center mb-6">
                            <h3 className="text-2xl font-bold text-[#073B4C]">Create a New Giveaway</h3>
                            <button onClick={() => setIsModalOpen(false)} className="text-2xl font-bold text-slate-500 hover:text-slate-800">&times;</button>
                        </div>
                        <form onSubmit={handleCreateCampaign}>
                            <div className="mb-4">
                                <label htmlFor="title" className="block text-sm font-medium text-slate-700">Campaign Title</label>
                                <input type="text" id="title" className="mt-1 block w-full px-3 py-2 bg-white border border-slate-300 rounded-md shadow-sm" required />
                            </div>
                            <div className="mb-4">
                                <label htmlFor="totalAmount" className="block text-sm font-medium text-slate-700">Total Giveaway Amount (₦)</label>
                                <input type="number" id="totalAmount" className="mt-1 block w-full px-3 py-2 bg-white border border-slate-300 rounded-md shadow-sm" required />
                            </div>
                            <div className="mb-4">
                                <label htmlFor="amountPerWinner" className="block text-sm font-medium text-slate-700">Amount per Winner (₦)</label>
                                <input type="number" id="amountPerWinner" className="mt-1 block w-full px-3 py-2 bg-white border border-slate-300 rounded-md shadow-sm" required />
                            </div>
                            <div className="mb-6">
                                <label htmlFor="giveawayMode" className="block text-sm font-medium text-slate-700">Giveaway Mode</label>
                                <select id="giveawayMode" value={mode} onChange={e => setMode(e.target.value)} className="mt-1 block w-full px-3 py-2 bg-white border border-slate-300 rounded-md shadow-sm">
                                    <option value="random">Random Selection</option>
                                    <option value="fcfs">First Come, First Served</option>
                                </select>
                            </div>
                            {mode === 'random' && (
                                <div className="mb-6">
                                    <label htmlFor="duration" className="block text-sm font-medium text-slate-700">Campaign Duration (minutes)</label>
                                    <input type="number" id="duration" className="mt-1 block w-full px-3 py-2 bg-white border border-slate-300 rounded-md shadow-sm" />
                                </div>
                            )}
                            <button type="submit" className="w-full bg-[#118AB2] text-white font-semibold py-3 rounded-lg hover:bg-[#0d6c8a]">Launch Campaign</button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};


// --- Main App Component ---
export default function App() {
    const [currentPage, setCurrentPage] = useState('login');
    const [authToken, setAuthToken] = useState(null);

    const navigateTo = (page) => {
        setCurrentPage(page);
    };

    const renderPage = () => {
        switch (currentPage) {
            case 'register':
                return <RegisterPage navigateTo={navigateTo} />;
            case 'dashboard':
                return <DashboardPage authToken={authToken} />;
            case 'login':
            default:
                return <LoginPage navigateTo={navigateTo} setAuthToken={setAuthToken} />;
        }
    };

    return (
        <div>
            {renderPage()}
        </div>
    );
}
